.swiper {
  width: 100%;
  height: 100%;
  margin: 0 auto;
  position: relative;
}

.swiper .swiper-slide .slide_title_container {
  color: #B58A4C;
  font-size: 150px;
  font-weight: bold;
  text-align: right;
  line-height: 1;
  font-family: 'Oswald', sans-serif;
  padding-top: 500px;
  width: 500px;
  float: left;
}

.swiper .swiper-slide>.slide_img {
  display: flex;
  justify-content: flex-end;
  animation: bounce 3s infinite ease-in-out;
}

.swiper .swiper-slide .slide_img>img {
  max-width: 100%;
}

@keyframes bounce {
  0% {
    transform: translateY(-5px);
  }

  50% {
    transform: translateY(-15px);
  }

  100% {
    transform: translateY(-5px);
  }
}

.left_aside_btn_container .swiper-button-next {
  position: absolute;
  top: 80%;
  left: 120px;
  color: rgba(255, 255, 255, 0.445);
}

.left_aside_btn_container .swiper-button-prev {
  position: absolute;
  top: 80%;
  left: 70px;
  color: rgba(255, 255, 255, 0.445);
}

@media (max-width: 1370px) {
  .swiper .swiper-slide>.slide_img {
    padding-top: 150px;
  }
}

@media (max-width: 1170px) {
  .swiper .swiper-slide .slide_title_container {
    font-size: 120px;
  }

  .swiper .swiper-slide>.slide_img {
    padding-top: 170px;
  }
}

@media (max-width: 960px) {
  .swiper .swiper-slide .slide_title_container {
    width: 300px;
    font-size: 80px;
    text-align: left;
  }

  .swiper .swiper-slide>.slide_img {
    padding-top: 200px;
  }
}

@media (max-width: 768px) {
  .swiper .swiper-slide .slide_title_container {
    width: 200px;
    font-size: 60px;
  }
}

@media (max-width: 570px) {
  .swiper .swiper-slide .slide_title_container {
    float: none;
    padding-top: 150px;
  }

  .swiper .swiper-slide>.slide_img {
    padding-top: 0;
  }
}

.our_slider {
  height: 700px;
}


.our_slider .our_slide {
  margin: 10px;
  padding: 40px 40px;
  text-align: center;
  background: #F8F7F5;
  box-shadow: 0px 0px 20px rgb(0 0 0 / 16%);
}

.our_slider .our_slide:last-child {
  margin: 0;
}

.slick-center .our_slide {
  transform: scale(1.2);
}

.our_slider .our_slide .our_slide_img img {
  margin: 0 auto;
}

.slick-center #center_text {
  color: #fff;
  background-color: #F15922;
}

.our_slider .our_slide .our_slide_text {
  background-color: #B58A4C;
  margin-top: 20px;
  padding: 10px;
}

.our_slider .our_slide .our_slide_text h3 {
  font-size: 24px;
  font-weight: bold;
  font-family: "Josefin Sans", sans-serif;
}

.our_slider .our_slide .our_slide_text h5 {
  font-size: 14px;
}

.slick-prev {
  position: relative;
  top: 50%;
  transform: translate(-50%);
  left: -20px;
  z-index: 1;
  border: 1px solid #B58A4C;
  padding: 10px;
  overflow: hidden;
}

.slick-next {
  position: absolute;
  top: 50%;
  right: -50px;
  z-index: 1;
  transform: translate(-50%);
  border: 1px solid #B58A4C;
  padding: 10px;
}

@media (max-width: 1400px) {
  .slick-prev {
    top: 45%;
  }

  .slick-next {
    top: 45%;
  }
}

@media (max-width: 1300px) {
  .slick-prev {
    top: 40%;
  }

  .slick-next {
    top: 40%;
  }
}

@media (max-width: 1100px) {
  .slick-prev {
    top: 35%;
  }

  .slick-next {
    top: 35%;
  }
}

@media (max-width: 1300px) {
  .slick-prev {
    top: 40%;
  }

  .slick-next {
    top: 40%;
  }
}

@media (max-width: 1020px) {
  .slick-prev {
    top: 50%;
  }

  .slick-next {
    top: 50%;
  }
}

@media (max-width: 550px) {
  .slick-prev {
    top: 45%;
  }

  .slick-next {
    top: 45%;
  }
}

@media (max-width: 500px) {
  .slick-prev {
    top: 40%;
  }

  .slick-next {
    top: 40%;
  }
}

.review_section {
  background-image: url(../img/bg.jpg);
  padding: 100px 0;
  position: relative;
}

.review_section .review_slider {
  width: 80%;
  margin: 0 auto;
  background-color: #fff;
  box-shadow: 0px 15px 30px rgb(181 138 76 / 30%);
  z-index: 3;
}

.review_section::before {
  position: absolute;
  top: 5%;
  left: 15%;
  content: "";
  width: 25%;
  height: 90%;
  border: 30px solid #B58A4C;
  z-index: 0;
}

.review_section .review_slider .review_slide {
  margin: 0 20px;
}

.review_section .review_slider .review_slide .review_slide_container {
  display: flex;
  justify-content: space-evenly;
  align-items: center;

}

.review_section .review_slider .review_slide .review_slide_text_box {
  width: 30%;
  padding: 100px 0;
  font-family: "Josefin Sans", sans-serif;
  font-weight: 600;
}

.review_section .review_slider .review_slide .review_slide_text_box p {
  line-height: 1.8;
  font-size: 18px;
}

.review_section .review_slider .review_slide .review_slide_text_box .review_slide_text_box_name {
  font-size: 26px;
  font-weight: bold;
  color: #F15922;
  margin-top: 30px;
}

.review_section .review_slider .review_slide .review_slide_text_box .review_slide_text_box_sub_name {}

.review_section .review_slider .review_slide .review_slide_img_box {}

.review_section .review_slider .review_slide .review_slide_img_box img {}

.review-prev {
  position: absolute;
  bottom: 40px;
  left: 120px;
  font-size: 27px;
  z-index: 1;
  transition: all 0.3s;
}

.review-next {
  position: absolute;
  bottom: 40px;
  left: 140px;
  font-size: 27px;
  z-index: 1;
  transition: all 0.3s;
}

.review-prev:hover {
  font-size: 30px;
}

.review-next:hover {
  font-size: 30px;
}

@media (max-width: 1260px) {
  .review_section::before {
    left: 13%;
  }
}

@media (max-width: 960px) {
  .review_section .review_slider .review_slide .review_slide_text_box p {
    line-height: 1.5;
    font-size: 17px;
  }

  .review_section .review_slider .review_slide .review_slide_text_box .review_slide_text_box_name {
    font-size: 22px;
  }
}

@media (max-width: 850px) {
  .review_section::before {
    left: 10%;
  }
}

@media (max-width: 660px) {
  .review_section .review_slider .review_slide .review_slide_container {
    flex-wrap: wrap;
    flex-direction: column;
  }

  .review_section .review_slider .review_slide .review_slide_text_box {
    width: 100%;
    padding: 20px 0 0;
    text-align: left;
  }

  .review_section .review_slider .review_slide .review_slide_img_box {
    width: 100%;
    margin-bottom: 60px;
  }

  .review_section .review_slider .review_slide .review_slide_img_box img {
    width: 100%;
    display: block;
  }

  .review-prev {
    position: absolute;
    bottom: 10px;
    left: 40px;
    font-size: 27px;
    z-index: 1;
    transition: all 0.3s;
  }

  .review-next {
    position: absolute;
    bottom: 10px;
    left: 60px;
    font-size: 27px;
    z-index: 1;
    transition: all 0.3s;
  }

  .review_section::before {
    width: 40%;
    left: 7%;
  }
}
